% Generate data; set variable inputSize equal to size of input vector of NNwarning('off','MATLAB:MKDIR:DirectoryExists');S = dir('./Data');existing = sum([S(~ismember({S.name},{'.','..'})).isdir]);for index = inputSize:(existing-1)  % put this first to stop errors    rmdir(['./Data/' int2str(index)], 's');endfor index = 0:(inputSize-1)    mkdir(['./Data/' int2str(index)]);  %create folder in /Data        %these were when trying to use .mat files for sequences    %a = matfile(['./Data/' int2str(index) '/signal.mat']);      %a.sig = oneHotFcn(index, inputSize);    %create and assign data to .mat    % this line allows to create new data without purging old folders    %a.Properties.Writable = true;        %creates an NxN matrix from N^2 input vector    matrixRep = reshape(oneHotFcn(index, inputSize), ...        [sqrt(inputSize), sqrt(inputSize)]);    %convert to bitmap image type for feeding to NN    imwrite(oneHotFcn(index, inputSize), ['./Data/' int2str(index) '/input.BMP']);end